import React, { useState } from 'react';

const initialMessagesList = [
  { content: "Hi! My name is Friendly Chatbot. Whatcha wanna talk about?", name: "Friendly Chatbot" },
  { content: "Yea its good ig", name: "saszyBe0tch79" },
  { content: "SLAY QUEEN YAAAs", name: "funfeminist" }
];

function Message({ content, name }) {
  return (
    <li>{content} <span>{name}</span></li>
  );
}

function Messages({ messages }) {
  return (
    <ul>
      {messages.map((msg, index) => (
        <Message key={index} content={msg.content} name={msg.name} />
      ))}
    </ul>
  );
}

function MessageBar({ onSendMessage }) {
  const [input, setInput] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    onSendMessage(input);
    setInput('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Enter text here..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button type="submit">Send</button>
    </form>
  );
}

function RoomName({ name }) {
  return <p>{name}</p>;
}

function RoomsList({ rooms }) {
  return (
    <ul>
      {rooms.map((room, index) => (
        <RoomName key={index} name={room.name} />
      ))}
    </ul>
  );
}

const ROOMS = [
  { name: "fun" },
  { name: "yoga" }
];

function CurrentRoom() {
  const [messages, setMessages] = useState(initialMessagesList);

  const handleSendMessage = (newMessage) => {
    setMessages([...messages, { content: newMessage, name: 'User' }]);
  };

  return (
    <div id="room">
      <h1>Welcome to Chat Room</h1>
      <Messages messages={messages} />
      <MessageBar onSendMessage={handleSendMessage} />
    </div>
  );
}

export default function App() {
  return <CurrentRoom />;
}
