import { API_BASE_URL } from '../constants/apiBaseUrl'

async function sayHello(text) {
    try {

      // passes text to server with translator
      const botTranslation = await fetch(`${API_BASE_URL}/api/chatbot/hello`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });

      // extract translated text
      const translationJson = await botTranslation.json();
      return translationJson.hello;

    } catch (error) {
      console.error("Error getting hello:", error); // handle error
    }

  }

  export { sayHello };
